<li style="background:url({$core_url}module/foxfeedspro/static/image/favorite.jpg) no-repeat left; padding-left:15px;padding-right:15px;">
	<a href="#?call=favorite.add&amp;height=100&amp;width=400&amp;type=foxfeedspro&amp;id={$item_id}" onclick="$('#js_favorite_link_unlike_{$item_id}').show(); $('#js_favorite_link_like_{$item_id}').hide();" class="inlinePopup" id="js_favorite_link_like_{$item_id}" title="{phrase var='foxfeedspro.add_to_your_favorite'}" {if $favorite_id} style="display:none;"{else}{/if}>{phrase var='foxfeedspro.favorite'}</a>
	<a title="{phrase var='foxfeedspro.remove_from_your_favorite'}" href="#" onclick="$('#js_favorite_link_like_{$item_id}').show(); $('#js_favorite_link_unlike_{$item_id}').hide(); $.ajaxCall('foxfeedspro.deleteFavorite', 'type=foxfeedspro&amp;id={$item_id}&amp;myfavorite={$bIsMyFavoritePage}', 'GET'); return false;" id="js_favorite_link_unlike_{$item_id}"{if $favorite_id} {else}style="display:none;"{/if}>{phrase var='foxfeedspro.unfavorite'}</a>
</li>